
public class Timetable
{

    /**
     * @param args
     */
    public static void main (String[] args)
    {
        for(int x=1; x<=3;x++)
        {
            for(int y=1; y<=4; y++)
            {
                System.out.print(x*y + " ");
            }

            System.out.println();
        }
    }
    
}
